/// <reference path="./typings.d.ts" />
import {
  FileBox,
} from './compact/index.js'
import { packageJson } from './package-json.js'

const VERSION = packageJson.version || '0.0.0'
const CONTACT_AVATAR = ''

const GROUP_AVATAR = ''

function avatarForGroup (): FileBox {
  return FileBox.fromDataURL(`data:image/png;base64,${GROUP_AVATAR}`, 'whatsapp_group')
}

function avatarForContact (): FileBox {
  return FileBox.fromDataURL(`data:image/png;base64,${CONTACT_AVATAR}`, 'whatsapp_contact')
}

const MEMORY_SLOT = 'PUPPET_WHATSAPP'
const PRE = 'PuppetWhatsApp'

export const MIN_BATTERY_VALUE_FOR_LOGOUT = Number(process.env['MIN_BATTERY_VALUE_FOR_LOGOUT']) || 1

export const LOGOUT_REASON = {
  BATTERY_LOWER_IN_PHONE: '手机电量过低，即将无法继续使用WhatsApp',
  DEFAULT: '退出登录',
  NETWORK_TIMEOUT_IN_PHONE: '手机端网络连接异常',
}

export {
  MEMORY_SLOT,
  avatarForGroup,
  avatarForContact,
  VERSION,
  PRE,
}
